public void jumpRef()
{
    Args args = new Args();
    args.caller(element);

    SysCorpNetPrinters networkPrinters;
    select firstonly networkPrinters where networkPrinters.PrinterName == this.text();
    FormRun formRun = new MenuFunction(menuItemDisplayStr(SysCorpNetPrinters), MenuItemType::Display).create(args);
    formRun.run();
    formRun.dataSource(1).positionToRecord(networkPrinters);
}

