    // Apply the selected filter
    UserInfo_ds.executeQuery();

    return ret;
}

public void lookup()
{
    SysTableLookup          sysTableLookup;
    Query                   query;
    QueryBuildDataSource    qbds;

    query = new Query();

    qbds = query.addDataSource(tableNum(SecurityRole));
    qbds.addOrderByField(fieldNum(SecurityRole, Name));

    sysTableLookup = SysTableLookup::newParameters(tableNum(SecurityRole), this);
    sysTableLookup.addLookupfield(fieldNum(SecurityRole, Name));
    sysTableLookup.addLookupfield(fieldNum(SecurityRole, Description));

    sysTableLookup.addSelectionField(fieldNum(SecurityRole, Name));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();

    super();
}

public boolean validate()
{
    boolean ret;

    ret = super();

    str selectedRoleName = SecurityroleName.text();
    SecurityRole selectedRole;
    select firstonly RecId from selectedRole where selectedRole.Name == selectedRoleName;
    if (!selectedRole.RecId)
    {
        ret = ret && checkFailed(strFmt("@DocentricAX4:InvalidRoleName", selectedRole));
    }

    return ret;
}

