Method.Source:
/// <summary>
/// Find a record on a given temp table by the provided RecId.
/// </summary>
/// <param name = "_recId">RecId to be found.</param>
/// <param name = "_utilsTable">DocPrintMgmtUtilsTmp temp table buffer where the record should be found.</param>
/// <param name = "_forUpdate">boolean if record should be selected for update.</param>
/// <returns>DocPrintMgmtUtilsTmp record that is found.</returns>
public static DocPrintMgmtUtilsTmp find(RecId _recId, DocPrintMgmtUtilsTmp _utilsTable, boolean _forUpdate = false)
{
    _utilsTable.selectForUpdate(_forUpdate);

    if (_recId)
    {
        select firstonly _utilsTable where _utilsTable.RecId == _recId;
    }

    return _utilsTable;
}

/// <summary>
/// Find a record on a given temp table by the provided PrintMgmtSettingsRecId.
/// </summary>
/// <param name = "_printMgmtSettingsRecId">PrintMgmtSettingsRecId to be found.</param>
/// <param name = "_utilsTable">DocPrintMgmtUtilsTmp temp table buffer where the record should be found.</param>
/// <param name = "_forUpdate">boolean if record should be selected for update.</param>
/// <returns>DocPrintMgmtUtilsTmp record that is found.</returns>
public static DocPrintMgmtUtilsTmp findByPrintMgmtSettingsRecId(RecId _printMgmtSettingsRecId, DocPrintMgmtUtilsTmp _utilsTable, boolean _forUpdate = false)
{
    _utilsTable.selectForUpdate(_forUpdate);

    if (_printMgmtSettingsRecId)
    {
        select firstonly _utilsTable where _utilsTable.PrintMgmtSettingsRecId == _printMgmtSettingsRecId;
    }

    return _utilsTable;
}

/// <summary>
/// Initialize the print management related fields from the PrintMgmtSettings record.
/// </summary>
/// <param name = "_printMgmtSettings">PrintMgmtSettings record.</param>
public void initFromPrintMgmtSettings(PrintMgmtSettings _printMgmtSettings)
{
    this.PrintMgmtSettingsRecId = _printMgmtSettings.RecId;
    this.PrintMgmtSettingsRecVersion = _printMgmtSettings.RecVersion;
    this.PrintSettingsDescription = _printMgmtSettings.Description;
    this.PrintSettingsNumberOfCopies = _printMgmtSettings.NumberOfCopies;
    this.PrintSettingsPriorityId = _printMgmtSettings.PriorityId;
    this.PrintJobSettings = _printMgmtSettings.PrintJobSettings;
}

/// <summary>
/// Initialize the document instance related fields from the PrintMgmtDocInstance record.
/// </summary>
/// <param name = "_docInstance">PrintMgmtDocInstance record.</param>
public void initFromPrintMgmtDocInstance(PrintMgmtDocInstance _docInstance)
{
    this.DocInstanceRecId = _docInstance.RecId;
    this.DocInstanceRecVersion = _docInstance.RecVersion;
    this.DocInstanceDocumentType = _docInstance.DocumentType;
    this.DocInstanceDocumentTypeName = enum2Str(_docInstance.DocumentType);
    this.DocInstanceName = _docInstance.Name;
    this.DocInstanceNodeType = _docInstance.NodeType;               

    switch (this.DocInstanceNodeType)
    {
        case PrintMgmtNodeType::Sales:
        case PrintMgmtNodeType::Purch:
        case PrintMgmtNodeType::Project:
        case PrintMgmtNodeType::Invent:
        case PrintMgmtNodeType::WHS:
        case PrintMgmtNodeType::TMS:
            this.DocInstanceNodeLevel = DocPrintMgmtNodeLevel::Module;
            break;

        case PrintMgmtNodeType::CustTable:
        case PrintMgmtNodeType::VendTable:
            this.DocInstanceNodeLevel = DocPrintMgmtNodeLevel::Account;
            break;

        default:
            this.DocInstanceNodeLevel = DocPrintMgmtNodeLevel::Transaction;
    }

    this.DocInstancePrintType = _docInstance.PrintType;
    this.DocInstancePriorityId = _docInstance.PriorityId;
    this.DocInstanceReferencedRecId = _docInstance.ReferencedRecId;
    this.DocInstanceReferencedTableId = _docInstance.ReferencedTableId;  
    this.DocInstanceSuppress = _docInstance.Suppress;
}

/// <summary>
/// Initialize the report format related fields from the PrintMgmtReportFormat record.
/// </summary>
/// <param name = "_reportFormat">PrintMgmtReportFormat record.</param>
public void initFromReportFormat(PrintMgmtReportFormat _reportFormat)
{
    if (!_reportFormat.RecId)
    {
        return;
    }

    this.FormatRecId = _reportFormat.RecId;
    this.FormatDescription = _reportFormat.Description;
    this.FormatName = _reportFormat.Name;
}

/// <summary>
/// Initialize the identification text from the PrintMgmtSettings record.
/// </summary>
/// <param name = "_printMgmtSettings">PrintMgmtSettings record</param>
public void initFromIdentificationText(PrintMgmtSettings _printMgmtSettings)
{        
    PrintMgmtIdentificationText footerText;
    select firstonly footerText where footerText.ParentId == _printMgmtSettings.RecId;

    this.IdentificationText = footerText.IdentificationText;        
}

/// <summary>
/// Update the print mgmt settings fields on the current record.
/// </summary>
/// <param name = "_printMgmtSettings">PrintMgmtSettings record</param>
public void rereadOneRecord(PrintMgmtSettings _printMgmtSettings)
{
    // PrintMgmtSettings fields
    this.initFromPrintMgmtSettings(_printMgmtSettings);

    // Footer text
    this.initFromIdentificationText(_printMgmtSettings);

    // Print destination fields
    DocPrintMgmtUtilsHelper::unpackPrintDestinationSettings(this);
}

/// <summary>
/// Create a full description of Print management setting, so that user understands
/// from where it comes. Use the existing Description if it exist, and append a prefix
/// Original/Copy/Conditional + indent the Conditional setting.
/// </summary>
/// <returns>string: description of Print management setting</returns>
private str getPrintSettingsDescriptionFull()
{
    str fullDescription = this.PrintSettingsDescription;

    if (fullDescription == '')
    {
        // Original and Copy (root document) have empty Description
        if (this.DocInstancePrintType == PrintMgmtDocInstanceType::Original)
        {
            fullDescription = 'Original <Default>';
        }
        else
        {
            fullDescription = 'Copy - ' + this.DocInstanceName;
        }
    }
    else
    {
        // Conditional setting must have non-empty Description
        if (this.DocInstancePrintType == PrintMgmtDocInstanceType::Original)
        {
            fullDescription = '--- Original --> Conditional <' + fullDescription + '>';
        }
        else
        {
            fullDescription = '--- Copy --> Conditional <' + fullDescription + '>';
        }
    }

    // Append the 'OVERRIDE' suffix for overrides
    if (this.DocInstanceNodeLevel != DocPrintMgmtNodeLevel::Module)
    {
        fullDescription += ' OVERRIDE';
    }

    return fullDescription;
}

/// <summary>
/// Populate one record based on the provided print management setting.
/// </summary>
/// <param name = "_printMgmtSettings">PrintMgmtSettings record</param>
/// <param name = "_docInstance">PrintMgmtDocInstance record</param>
/// <param name = "_reportFormat">PrintMgmtReportFormat record</param>
/// <param name = "_module">PrintMgmtNodeType module type</param>
public void fillOneRecord(PrintMgmtSettings _printMgmtSettings,
                            PrintMgmtDocInstance _docInstance,
                            PrintMgmtReportFormat _reportFormat,
                            PrintMgmtNodeType _module)
{
    this.clear();

    // PrintMgmtSettings fields
    this.initFromPrintMgmtSettings(_printMgmtSettings);

    // PrintMgmtDocInstance fields
    this.initFromPrintMgmtDocInstance(_docInstance);

    // PrintMgmtReportFormat fields
    this.initFromReportFormat(_reportFormat);

    // Print mgmt module
    this.PrintMgmtModule = _module;

    // Node-specific ID
    this.NodeSpecificId = DocPrintMgmtUtilsHelper::getNodeInfo(_docInstance.ReferencedTableId, _docInstance.ReferencedRecId, _printMgmtSettings.DataAreaId).parmNodeSpecificId();           

    // Full description
    this.PrintSettingsDescriptionFull = this.getPrintSettingsDescriptionFull();

    // Footer text
    this.initFromIdentificationText(_printMgmtSettings);

    // Print destination fields
    DocPrintMgmtUtilsHelper::unpackPrintDestinationSettings(this);

    this.insert();

    // Validate the record, so that when PrintMgmtUtils form opens we have invalid records marked
    this.validate();
}

/// <summary>
/// Populate the given DocPrintMgmtUtilsTmp table with detailed (flat, unpacked) information
/// about the related print management setting and print destination setting.
/// </summary>
/// <param name = "_tmpTable">DocPrintMgmtUtilsTmp buffer to be populated.</param>
/// <param name = "_infoTable">DocPrintMgmtInfoTmp table with document type, module and report links
/// (helper for populating the information about the print management module).</param>
/// <param name = "_fillForAllDocumentTypes">boolean if true, fill for all document types,
/// otherwise only for the _documentTypeToFill document type.</param>
/// <param name = "_documentTypeToFill">PrintMgmtDocumentType document type for which
/// to fill the table.</param>
/// <returns>int number of added records</returns>
public static int fillTableWithSettings(DocPrintMgmtUtilsTmp _tmpTable, 
                                        DocPrintMgmtInfoTmp _infoTable,
                                        boolean _fillForAllDocumentTypes,
                                        PrintMgmtDocumentType _documentTypeToFill)
{        
    int cnt = 0; 

    PrintMgmtSettings printMgmtSettings;
    PrintMgmtDocInstance docInstance;
    PrintMgmtReportFormat reportFormat, reportFormatSelected, reportFormatSystem;

    Set nodeTypesAllowedForUser = DocPrintMgmtHelper::getNodeTypesAllowedForUser();

    // Cleanup the previous table version.
    // If we need to fill for all document types, then delete everything.
    // Otherwise delete only the records for the given _documentTypeToFill.
    if (_fillForAllDocumentTypes)
    {
        delete_from _tmpTable;
    }
    else
    {
        delete_from _tmpTable where _tmpTable.DocInstanceDocumentType == _documentTypeToFill;
    }

    while select printMgmtSettings
        join docInstance
            where docInstance.RecId == printMgmtSettings.ParentId
                && (_fillForAllDocumentTypes || (!_fillForAllDocumentTypes && docInstance.DocumentType == _documentTypeToFill))
        join _infoTable 
            where _infoTable.DocumentType == docInstance.DocumentType
        outer join reportFormatSelected // format selected in UI
            where reportFormatSelected.RecId == printMgmtSettings.ReportFormat
        outer join reportFormatSystem   // default format for this document type
            where reportFormatSystem.DocumentType == docInstance.DocumentType
                && reportFormatSystem.System == NoYes::Yes
    {
        // Skip the modules not allowed for the current user.
        if (!nodeTypesAllowedForUser.in(_infoTable.NodeType))
        {
            continue;
        }

        // Settings in WHS and TMS modules have ReportFormat = 0, even though the format is selected 
        // in the Print mgmt setup form. In that case we will take the default format for this document type.
        reportFormat = printMgmtSettings.ReportFormat == 0 ? reportFormatSystem : reportFormatSelected;

        _tmpTable.fillOneRecord(printMgmtSettings, docInstance, reportFormat, _infoTable.NodeType);

        cnt++;
    }

    return cnt;
}

/// <summary>
/// Copied logic from Print destination form.
/// Get the info if the Template Id on the record is configured for the SSRS design.
/// </summary>
/// <returns>boolean if Template Id is SSRS design, false for Docentric design.</returns>
public boolean useSrsBuiltInDesign()
{
    return DocPrintMgmtHelper::useSrsBuiltInDesign(this.DestDocTemplateId);
}

/// <summary>
/// Called when the value of the field is changed.
/// </summary>
/// <param name = "_fieldId">Field Id</param>
public void modifiedField(FieldId _fieldId)
{
    super(_fieldId);

    switch (_fieldId)
    {
        // Print medium type
        case fieldNum(DocPrintMgmtUtilsTmp, PrintMediumType):
            this.modifiedPrintMediumType();
            break;

        // Template
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocTemplateId):
            this.modifiedDocFileName();
            this.modifiedDocEmailAttachmentName();
            this.modifiedPrintDestDescriptionPart();
            break;

        // Docentric File print destination, filename elements.
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocFileName):
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocFileFormat):
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocFileFormatSrs):
            this.modifiedDocFileName();
            this.modifiedPrintDestDescriptionPart();
            break;

        // Docentric File print destination, Azure Blob
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocFileAzureBlobPath):
            this.modifiedAzureBlobPath();
            break;
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocFileAzureBlobStorageAccountId):
            this.modifiedAzureBlobStorageAcount();
            break;

        // Docentric File print destination, Azure Files
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocFileAzureFilesFolderPath):
            this.modifiedAzureFilesFolderPath();
            break;
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocFileAzureFilesAccountId):
            this.modifiedAzureFilesAccountId();
            break;

        // Docentric File print destination, SharePoint
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocFileSharePointFolderPath):
            this.modifiedSharePointFolderPath();
            break;

        // Docentric Email print destination, email template elements.
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocEmailUseEmailTemplate):
            this.modifiedUseEmailTemplate();
            break;
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocEmailTemplateId):
            this.modifiedEmailTemplateId();
            break;

        // Docentric Email print destination, filename elements.
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocEmailAttachmentName):
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocEmailAttachmentFileFormat):
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocEmailAttachmentFileFormatSrs):
            this.modifiedDocEmailAttachmentName();
            this.modifiedPrintDestDescriptionPart();
            break;

        // Docentric Email print destination, email addresses (To, Cc, Bcc, From).
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocEmailTo):
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocEmailCc):
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocEmailBcc):
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocEmailFrom):
            this.modifiedDocEmailAddress(_fieldId);
            if (_fieldId == fieldNum(DocPrintMgmtUtilsTmp, DestDocEmailTo))
            {
                this.modifiedPrintDestDescriptionPart();
            }
            break;

        // Docentric Printer print destination
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocPrinterNumberOfCopies):
            this.modifiedPrinterNumberOfCopies(_fieldId);
            break;
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocPrinterPrintFromPage):
            this.modifiedPrinterFromPage(_fieldId, fieldNum(DocPrintMgmtUtilsTmp, DestDocPrinterPrintToPage));
            break;
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocPrinterPrintToPage):
            this.modifiedPrinterToPage(fieldNum(DocPrintMgmtUtilsTmp, DestDocPrinterPrintFromPage), _fieldId);
            break;
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocPrinterPrintAsPdfFilename):
            this.modifiedPrinterPrintAsPdfFilename();
            break;

        // SRS File print destination
        case fieldNum(DocPrintMgmtUtilsTmp, DestSSRSFileName):
        case fieldNum(DocPrintMgmtUtilsTmp, DestSSRSFileFormat):
            this.modifiedSRSFileNameOrFormat();
            this.modifiedPrintDestDescriptionPart();
            break;

        // SRS Printer print destination
        case fieldNum(DocPrintMgmtUtilsTmp, DestSSRSPrinterNumberOfCopies):
            this.modifiedPrinterNumberOfCopies(_fieldId);
            break;
        case fieldNum(DocPrintMgmtUtilsTmp, DestSSRSPrinterPrintFromPage):
            this.modifiedPrinterFromPage(_fieldId, fieldNum(DocPrintMgmtUtilsTmp, DestSSRSPrinterPrintToPage));
            break;
        case fieldNum(DocPrintMgmtUtilsTmp, DestSSRSPrinterPrintToPage):
            this.modifiedPrinterToPage(fieldNum(DocPrintMgmtUtilsTmp, DestSSRSPrinterPrintFromPage), _fieldId);
            break;
    }

    switch (_fieldId) 
    {
        // If some of the fields that are combined into the Print destination description
        // is modified, then get the new description.
        // In addition to the fields handled below, also the following fields are used 
        // for building the PD description, but they are specifically handled above, 
        // so we will skip them here:
        //      DestDocTemplateId,
        //      DestDocFileName, DestDocFileFormat, 
        //      DestSSRSFileName, DestDocFileFormatSrs
        //      DestDocEmailTo
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocSaveInPrintArchive):
        case fieldNum(DocPrintMgmtUtilsTmp, DestDocPrinterName):
        case fieldNum(DocPrintMgmtUtilsTmp, DestSSRSEmailTo):
        case fieldNum(DocPrintMgmtUtilsTmp, DestSSRSPrinterName):
            this.modifiedPrintDestDescriptionPart();
            break;
    }

    // Only unpacked fields are editable. When some of them is changed, 
    // set the Packed-unpacked synchronization status to NOT synced.
    // leaveRecord() method will make sure to sync all changes to
    // the packed container on this record.
    this.UnpackedSyncStatus = DocPrintMgmtUtilsUnpackedSyncStatus::NotSynced;

    // Mark record as dirty.
    this.RecordStatus = DocPrintMgmtUtilsRecordStatus::Dirty;
}

/// <summary>
/// A display method for validation status indicator.
/// </summary>
/// <returns>Record validation status indicator</returns>
public display container validationStatusIndicator()
{
    container result = conNull();

    container symbolAndLabel = this.getSymbolAndLabelForValidationStatus();
    str symbol = conPeek(symbolAndLabel, 1);
    str label = conPeek(symbolAndLabel, 2);

    if (symbol)
    {
        result = ImageReference::constructForSymbol(symbol, label).pack();
    }
    return result;
}

/// <summary>
/// Gets the symbol and label depending on current validation status.
/// </summary>
/// <returns>Container with symbol and label</returns>
private container getSymbolAndLabelForValidationStatus()
{
    str symbol = '';
    LabelId label;

    switch (this.ValidationStatus)
    {
        case DocPrintMgmtUtilsValidationStatus::Valid:
            symbol = ImageReferenceSymbol::Completed;
            label = "@DocentricAX4:ValidationStatusValid";
            break;

        case DocPrintMgmtUtilsValidationStatus::InvalidPrintMgmtSettings:
            symbol = ImageReferenceSymbol::Error;
            label = "@DocentricAX4:ValidationStatusInvalidPrintMgmt";
            break;

        case DocPrintMgmtUtilsValidationStatus::InvalidPrintDestinationSettings:
            symbol = ImageReferenceSymbol::Error;
            label = "@DocentricAX4:ValidationStatusInvalidPrintDestination";
            break;

        case DocPrintMgmtUtilsValidationStatus::InvalidBoth:
            symbol = ImageReferenceSymbol::Error;
            label = "@DocentricAX4:ValidationStatusInvalidBoth";
            break;
    }
    return [symbol, label];
}

/// <summary>
/// Validate the current record. We validate separately the Print management setting
/// and Print destination. In case one or both of them are invalid, we populate the
/// ValidationErrorList field with list of all validation errors and we set the
/// ValidationStatus field accordingly. 
/// </summary>
/// <returns>True if record is valid, false otherwise.</returns>
public boolean validate()
{ 
    boolean isPrintMgmtSettingValid = true;
    boolean isSrsPdValid = true, isDocentricPdValid = true;
    str validationMessage = '';
    DocPrintMgmtUtilsValidationStatus validationStatus = DocPrintMgmtUtilsValidationStatus::Valid;

    const str delimiterLine = '\n--------------------------------------------\n';
    const str emptyLine = '\n';

    int infologLine = infologLine();


    /* 
     * ------------------------------------------
     * Step 1: check the Print management setting
     * ------------------------------------------
     */
    isPrintMgmtSettingValid = DocPrintMgmtHelper::validatePrintManagementSetting(this.PrintMgmtSettingsRecId);        

    if (!isPrintMgmtSettingValid)
    {
        validationStatus = DocPrintMgmtUtilsValidationStatus::InvalidPrintMgmtSettings;
        validationMessage = "@DocentricAX4:TitleInvalidPrintMgmt" + delimiterLine;
        validationMessage += DocGlobalHelper::getInfoLogMessages(infologLine + 1, '', true);
    }


    /*
    * ------------------------------------------
    * Step 2: check the print destination
    * ------------------------------------------
    */

    // We validate the staging data, so make sure that packed and unpacked values are in sync.
    if (this.UnpackedSyncStatus == DocPrintMgmtUtilsUnpackedSyncStatus::NotSynced)
    {
        DocPrintMgmtUtilsHelper::packAndSavePrintDestinationSettings(this);
    }
    SRSPrintDestinationSettings pds = new SRSPrintDestinationSettings();
    pds.unpack(this.PrintJobSettings);

    switch (pds.printMediumType())
    {            
        case SRSPrintMediumType::Screen:
        case SRSPrintMediumType::Printer:
        case SRSPrintMediumType::File:
        case SRSPrintMediumType::Email:
            // Call the SSRS validation.
            // It throws error, so we need to catch it.
            try
            {
                pds.validate();
            }
            catch
            {
                isSrsPdValid = false;
            }
            break;

        case SRSPrintMediumType::Screen_DC:
            // Do nothing. There is nothing that needs to be supplied by the user to print to screen.
            break;

        case SRSPrintMediumType::GenerateDataSource_DC:
            DocPrintDestSettingsGenerateDS pdGenerateDS = pds.parmGenerateDSPrintDestSettings_DC();
            isDocentricPdValid = pdGenerateDS.validate(true);
            break;

        case SRSPrintMediumType::Email_DC:
            DocPrintDestSettingsEmail pdEmail = pds.parmEmailPrintDestSettings_DC();
            isDocentricPdValid = pdEmail.validate(this.FormatName, true, false, false);
            break;

        case SRSPrintMediumType::File_DC:
            DocPrintDestSettingsFile pdFile = pds.parmFilePrintDestSettings_DC();
            isDocentricPdValid = pdFile.validate(true, false);
            break;

        case SRSPrintMediumType::Printer_DC:
            DocPrintDestSettingsPrinter pdPrinter = pds.parmPrinterPrintDestSettings_DC();
            isDocentricPdValid = pdPrinter.validate(true, false, false);
            break;

        case SRSPrintMediumType::Custom:
        case SRSPrintMediumType::PowerBI:
        case SRSPrintMediumType::Memory_DC:
            DocGlobalHelper::handleWarning(strFmt("@DocentricAX4:ValidationErrorInvalidPrintMediumType", enum2Str(pds.printMediumType())));
            isDocentricPdValid = false;
            break;
    }


    /*
    * --------------------------------------------
    * Step 3: prepare and save the validation info
    * --------------------------------------------
    */

    if (!isSrsPdValid || !isDocentricPdValid)
    {
        validationStatus = (validationStatus == DocPrintMgmtUtilsValidationStatus::Valid) ?
                                DocPrintMgmtUtilsValidationStatus::InvalidPrintDestinationSettings :
                                DocPrintMgmtUtilsValidationStatus::InvalidBoth;

        if (validationMessage != '')
        {
            validationMessage += emptyLine + emptyLine;
        }

        validationMessage += "@DocentricAX4:TitleInvalidPrintDest" + delimiterLine;
        validationMessage += DocGlobalHelper::getInfoLogMessages(infologLine + 1, '', true);
    }


    this.ValidationErrorList = validationMessage;
    this.ValidationStatus = validationStatus;

    ttsbegin;
    this.update();
    ttscommit;

    return (validationStatus == DocPrintMgmtUtilsValidationStatus::Valid);
}

