/// <summary>
/// Called when some of the values that are used for building the Print destination description
/// is modified, to force building the new description.
/// </summary>
private void modifiedPrintDestDescriptionPart()
{
    // Get print destination settings from current record
    SRSPrintDestinationSettings pds = this.DocPrintDestinationSettingsMap::getPrintDestinationSettingsFromRecord();
    this.PrintDestinationDescription = DocSrsReportRunUtil::getPrintDestDescription(pds);
}

/// <summary>
/// Called when Print medium type field is modified. We perform the validation, because
/// some print destinations aren't offered in the Print destination form, so we don't allow
/// them here.
/// </summary>
private void modifiedPrintMediumType()
{
    switch (this.printMediumType)
    {
        case SRSPrintMediumType::Custom:
        case SRSPrintMediumType::PowerBI:
        case SRSPrintMediumType::Memory_DC:
            DocGlobalHelper::handleWarning(strFmt("@DocentricAX4:ValidationErrorInvalidPrintMediumType", enum2Str(this.printMediumType)));
            break;

        default:
        // do nothing
    }
}

