// DOCENTRIC FILE print destination - fields modified
////////////////////////////////////////////////////////////

/// <summary>
/// Copied and adjusted from Print destination form.
/// Execute the processing necessary when Docentric File print destination filename
/// is modified. This method is also called when file extension (for any of the
/// template types, SSRS/Docentric) is modified.
/// </summary>
private void modifiedDocFileName()
{
    Filename    currentSelectedFilepath, currentSelectedFileName, currentSelectedFileType;
    Filename    reportFilenameExt;
    boolean     updateOutputFilename = true;

    [currentSelectedFileName, currentSelectedFileType, currentSelectedFilepath] = Docu::splitFilename(this.DestDocFileName);

    // Output filename and format
    if (this.useSrsBuiltInDesign())
    {
        reportFilenameExt = DocFileMngHelper::convertSrsFileFormatToFileExtensionStr(this.DestDocFileFormatSrs);
        //updateOutputFilename = true;
    }
    else
    {
        reportFilenameExt = DocFileMngHelper::convertToFileExtensionStr(this.DestDocFileFormat);
        //updateOutputFilename = reportFilenameExt != DocConstantFile::FileExtXml || !currentSelectedFileType;
    }

    if (currentSelectedFileName)
    {
        // Change the report output filename to have the correct file extension which matches the selected output file format.
        if ((currentSelectedFileType != reportFilenameExt) && updateOutputFilename)
        {
            Filename newReportFilename = currentSelectedFileName + DocConstantFile::FileExtensionDelimiter + reportFilenameExt;
            this.DestDocFileName = newReportFilename;
        }
    }
    else
    {
        this.DestDocFileName = '';
    }

    // Azure blob, Azure files
    if (this.DestDocFileSaveToAzureBlob)
    {
        this.modifiedAzureBlobPath();
    }
    if (this.DestDocFileSaveToAzureFiles)
    {
        this.modifiedAzureFilesFolderPath();
    }
}

/// <summary>
/// Copied and adjusted from Print destination form.
/// Called when Azure Blob storage Account Id is modified.
/// </summary>
private void modifiedAzureBlobStorageAcount()
{
    // Check if an external Azure storage account is selected.
    if (this.DestDocFileAzureBlobStorageAccountId != '')
    {
        // If there is no valid licence, user will be warned, but we will still let him select an external Azure storage account.
        // Later in the pipeline we will throw the exception if the external Azure storage account is used, but the license is invalid.
        DocLicenseMng::checkLicenseUI(DocConstant::FeatureSavingToExternalAzureBlobStorage, formStr(DocPrintMgmtUtils));
    }
}

/// <summary>
/// Copied and adjusted from Print destination form.
/// Called when Docentric File print destination / Azure Blob storage account ID is modified.
/// </summary>
private void modifiedAzureBlobPath()
{
    this.DestDocFileAzureBlobPath = DocAzureBlobHelper::getAzureBlobPath(this.DestDocFileAzureBlobPath);
}

/// <summary>
/// Copied and adjusted from Print destination form.
/// Called when Azure Files Account Id is modified.
/// </summary>
private void modifiedAzureFilesAccountId()
{
    // Check if an external Azure files storage account is selected.
    if (this.DestDocFileAzureFilesAccountId != '')
    {
        // If there is no valid licence, user will be warned, but we will still let him select an external Azure files storage account.
        // Later in the pipeline we will throw the exception if the external Azure files storage account is used, but the license is invalid.
        DocLicenseMng::checkLicenseUI(DocConstant::FeatureSavingToExternalAzureFiles, formStr(DocPrintMgmtUtils));
    }
}

/// <summary>
/// Copied and adjusted from Print destination form.
/// Called when Docentric File print destination / Azure Files folder path is modified.
/// </summary>
private void modifiedAzureFilesFolderPath()
{
    this.DestDocFileAzureFilesFolderPath = DocAzureFilesHelper::getAzureFilesFolderPath(this.DestDocFileAzureFilesFolderPath);
}

/// <summary>
/// Copied and adjusted from Print destination form.
/// Called when SharePoint folder path is modified.
/// </summary>
private void modifiedSharePointFolderPath()
{
    this.DestDocFileSharePointFolderPath = DocSharePointHelperV3::getSharePointFolderPath(this.DestDocFileSharePointFolderPath);
}

////////////////////////////////////////////////////////////
// DOCENTRIC EMAIL print destination - fields modified
////////////////////////////////////////////////////////////

/// <summary>
/// Copied and adjusted from Print destination form.
/// Called when Docentric Email attachment name is modified.
/// </summary>
private void modifiedDocEmailAttachmentName()
{
    Filename    currentSelectedAttachmentFilepath, currentSelectedAttachmentFileName, currentSelectedAttachmentFileType;
    Filename    attachmentFilenameExt;
    boolean     updateOutputFilename = true;

    [currentSelectedAttachmentFileName, currentSelectedAttachmentFileType, currentSelectedAttachmentFilepath] = Docu::splitFilename(this.DestDocEmailAttachmentName);

    if (this.useSrsBuiltInDesign())
    {
        attachmentFilenameExt = DocFileMngHelper::convertSrsFileFormatToFileExtensionStr(this.DestDocEmailAttachmentFileFormatSrs);
        //updateOutputFilename = true;
    }
    else
    {
        attachmentFilenameExt = DocFileMngHelper::convertToFileExtensionStr(this.DestDocEmailAttachmentFileFormat);
        //updateOutputFilename = attachmentFilenameExt != DocConstantFile::FileExtXml || !currentSelectedAttachmentFileType;
    }

    if (this.DestDocEmailAttachmentName)
    {
        if (currentSelectedAttachmentFileType != attachmentFilenameExt && updateOutputFilename)
        {
            Filename newAttachmentFilename = currentSelectedAttachmentFileName + DocConstantFile::FileExtensionDelimiter + attachmentFilenameExt;
            this.DestDocEmailAttachmentName = newAttachmentFilename;
        }
    }
}

/// <summary>
/// Copied and adjusted from Print destination form.
/// Called when some of the Docentric Email addresses (To, Cc, Bcc, From) is modified.
/// </summary>
/// <param name = "_fieldId">FieldId that was modified.</param>
private void modifiedDocEmailAddress(FieldId _fieldId)
{
    str emailAddressList;

    // Cleanup the email address list.
    switch (_fieldId)
    {
        case (fieldNum(DocPrintMgmtUtilsTmp, DestDocEmailTo)):
            emailAddressList = DocEmailMngHelper::deleteEmptyAndDuplicatedEmailAddresses(this.DestDocEmailTo);
            if (emailAddressList != this.DestDocEmailTo)
            {
                this.DestDocEmailTo = emailAddressList;
            }
            break;

        case (fieldNum(DocPrintMgmtUtilsTmp, DestDocEmailCc)):
            emailAddressList = DocEmailMngHelper::deleteEmptyAndDuplicatedEmailAddresses(this.DestDocEmailCc);
            if (emailAddressList != this.DestDocEmailCc)
            {
                this.DestDocEmailCc = emailAddressList;
            }
            break;

        case (fieldNum(DocPrintMgmtUtilsTmp, DestDocEmailBcc)):
            emailAddressList = DocEmailMngHelper::deleteEmptyAndDuplicatedEmailAddresses(this.DestDocEmailBcc);
            if (emailAddressList != this.DestDocEmailBcc)
            {
                this.DestDocEmailBcc = emailAddressList;
            }
            break;

        case (fieldNum(DocPrintMgmtUtilsTmp, DestDocEmailFrom)):
            emailAddressList = DocEmailMngHelper::deleteEmptyAndDuplicatedEmailAddresses(this.DestDocEmailFrom);
            if (emailAddressList != this.DestDocEmailFrom)
            {
                this.DestDocEmailFrom = emailAddressList;
            }
            break;
    }


    // Validate email addresses.
    if (_fieldId == fieldNum(DocPrintMgmtUtilsTmp, DestDocEmailFrom))
    {
        // From field: validate email address list if there are no placeholders.
        if (emailAddressList && !DocPlaceholderManager::findAnyPlaceholderCandidateForEmails(emailAddressList, false))
        {
            if (!DocGlobalHelper::validateEmail(emailAddressList))
            {
                DocGlobalHelper::handleWarning(strFmt("@DocentricAX:FromEmailAddressesInvalid", emailAddressList));
            }
        }
    }
    // Other email address fields: validate email address list but ignore placeholders.
    else if (emailAddressList && !DocGlobalHelper::validateEmailListSkipPlaceholders(emailAddressList))
    {
        switch (_fieldId)
        {
            case (fieldNum(DocPrintMgmtUtilsTmp, DestDocEmailTo)):
                DocGlobalHelper::handleWarning(strFmt("@DocentricAX:ToEmailAddressesNotValid", emailAddressList));
                break;

            case (fieldNum(DocPrintMgmtUtilsTmp, DestDocEmailCc)):
                DocGlobalHelper::handleWarning(strFmt("@DocentricAX:CcEmailAddressesNotValid", emailAddressList));
                break;

            case (fieldNum(DocPrintMgmtUtilsTmp, DestDocEmailBcc)):
                DocGlobalHelper::handleWarning(strFmt("@DocentricAX:BccEmailAddressesNotValid", emailAddressList));
                break;
        }
    }
}

/// <summary>
/// Copied and adjusted from Print destination form.
/// Called when Docentric Email use email template is modified.
/// </summary>
/// <param name = "_resetSelectedEmailTemplate">Should we reset the value of the SelectEmailTemplate</param>
private void modifiedUseEmailTemplate(boolean _resetSelectedEmailTemplate = true)
{
    if (this.DestDocEmailUseEmailTemplate)  // 'Use email template' is turned on
    {
        // If there is no valid licence, user will be warned, but we will still let him select an email template.
        // Later in the pipeline we will throw the exception if the email template is used, but the license is invalid.
        DocLicenseMng::checkLicenseUI(DocConstant::FeatureEmailTemplates, 'DocPrintMgmtUtils');

        if (this.DestDocEmailBody != '')
        {
            BoxFormOnce boxFormOnce = BoxFormOnce::construct();
            boxFormOnce.parmDialogBoxType(DialogBoxType::YesNoBox);
            boxFormOnce.parmDialogButton(DialogButton::No);
            boxFormOnce.parmOwner(strFmt('%1 - %2 - 1', 'DocPrintMgmtUtils', DocConstant::FeatureEmailTemplates));
            boxFormOnce.parmText("@DocentricAX3:EmailTemplateSetUseEmailTemplateOn");
            boxFormOnce.parmTitle("@DocentricAX3:EmailTemplateCaption");
            if (boxFormOnce.prompt() == DialogButton::No)
            {
                this.DestDocEmailUseEmailTemplate = NoYes::No;
                this.modifiedUseEmailTemplate(false);
                return;
            }
        }

        this.DestDocEmailTemplateId = '';
        this.modifiedEmailTemplateId();
    }
    else  // 'Use email template' is turned off
    {
        if (_resetSelectedEmailTemplate)
        {
            this.DestDocEmailTemplateId = '';
            this.modifiedEmailTemplateId();
        }
    }
}

/// <summary>
/// Copied and adjusted from Print destination form.
/// Called when Docentric Email email template Id is modified.
/// </summary>
private void modifiedEmailTemplateId()
{
    DocReportEmailTemplateHtmlBody htmlBody = '';

    if (this.DestDocEmailTemplateId)    // This is true when 'Use email template' is turned on.
    {
        // Get the email template record from the setup for the selected email template id.
        DocReportEmailTemplate docReportEmailTemplate = DocReportEmailTemplate::tryGetEmailTemplate(this.FormatName, this.DestDocEmailTemplateId, curExt(), currentUserLanguage());
        htmlBody = docReportEmailTemplate.HtmlBody;
    }

    this.DestDocEmailBody = htmlBody;
}

