// DOCENTRIC PRINTER print destination - fields modified
////////////////////////////////////////////////////////////

/// <summary>
/// Copied and adjusted from Print destination form.
/// Called when Docentric Printer from page or SRS Printer from page is modified.
/// </summary>
/// <param name = "_fromPageFieldId">Printer from page field Id</param>
/// <param name = "_toPageFieldId">Printer to page field Id</param>
private void modifiedPrinterFromPage(FieldId _fromPageFieldId, FieldId _toPageFieldId)
{
    if (this.(_fromPageFieldId) <= 0)
    {
        this.(_fromPageFieldId) = 1;
    }

    if (this.(_fromPageFieldId) > this.(_toPageFieldId))
    {
        this.(_toPageFieldId) = this.(_fromPageFieldId);
    }
}

/// <summary>
/// Copied and adjusted from Print destination form.
/// Called when Docentric Printer to page or SRS Printer to page is modified.
/// </summary>
/// <param name = "_fromPageFieldId">Printer from page field Id</param>
/// <param name = "_toPageFieldId">Printer to page field Id</param>
private void modifiedPrinterToPage(FieldId _fromPageFieldId, FieldId _toPageFieldId)
{
    if (this.(_toPageFieldId) <= 0)
    {
        this.(_toPageFieldId) = 1;
    }

    if (this.(_toPageFieldId) < this.(_fromPageFieldId))
    {
        this.(_toPageFieldId) = this.(_fromPageFieldId);
    }
}

/// <summary>
/// Copied and adjusted from Print destination form.
/// Called when Docentric Printer number of copies or SRS Printer number of copies is modified.
/// </summary>
/// <param name = "_numberOfCopiesFieldId">Printer number of copies field Id</param>
private void modifiedPrinterNumberOfCopies(FieldId _numberOfCopiesFieldId)
{
    if (_numberOfCopiesFieldId == fieldNum(DocPrintMgmtUtilsTmp, DestSSRSPrinterNumberOfCopies))
    {
        this.DestSSRSPrinterOverridePageSettings = true;
    }
    if (this.(_numberOfCopiesFieldId) <= 0)
    {
        this.(_numberOfCopiesFieldId) = 1;
    }
}

/// <summary>
/// Copied and adjusted from Print destination form.
/// Called when Docentric Printer print as PDF filename is modified.
/// </summary>
private void modifiedPrinterPrintAsPdfFilename()
{
    Filename    currentSelectedFilepath, currentSelectedFileName, currentSelectedFileType;

    [currentSelectedFileName, currentSelectedFileType, currentSelectedFilepath] = DocFileMngHelper::splitFilename(this.DestDocPrinterPrintAsPdfFilename);

    if (currentSelectedFileName)
    {
        if (currentSelectedFileType != DocConstantFile::FileExtPdf)
        {
            Filename newReportFilename = currentSelectedFileName + DocConstantFile::FileExtensionDelimiter + DocConstantFile::FileExtPdf;
            this.DestDocPrinterPrintAsPdfFilename = newReportFilename;
        }
    }
    else
    {
        this.DestDocPrinterPrintAsPdfFilename = '';
    }
}

