// SSRS FILE print destination - fields modified
////////////////////////////////////////////////////////////

/// <summary>
/// Copied and adjusted from Print destination form.
/// Called when SSRS File name or File format is modified.
/// </summary>
private void modifiedSRSFileNameOrFormat()
{
    Filename path;
    Filename name;
    Filename ext;

    if (this.DestSSRSFileName)
    {
        [path, name, ext] = fileNameSplit(this.DestSSRSFileName);

        if (name)
        {
            this.DestSSRSFileName = path + name + SRSPrintDestinationSettings::findFileNameType(this.DestSSRSFileFormat, SRSImageFileFormat::TIFF);
        }
        else
        {
            this.DestSSRSFileName = path;
        }
    }
}
