/// <summary>
/// Placeholder constants for the Email Templates functionality.
/// </summary>
class DocConstantPlaceholderET
{
    // Placeholder symbols.
    public const str PlaceholderStartSymbol = '%';
    public const str PlaceholderEndSymbol = '%';

    // Hidden placeholders marking table begin and end, which should be used
    // in the source HTML of email message templates only.
    public const str HiddenPlaceholderTableBeginTag = '<!--%HIDDEN_TABLEBEGIN%-->';
    public const str HiddenPlaceholderTableEndTag = '<!--%HIDDEN_TABLEEND%-->';
    public const str PlaceholderTableBegin = 'HIDDEN_TABLEBEGIN';
    public const str PlaceholderTableEnd = 'HIDDEN_TABLEEND';


    // Standard placeholders.
    public const DocPlaceholderName PlaceholderEmailId = 'EMAILID';
    public const DocPlaceholderName PlaceholderEmailDescription = 'EMAILDESCRIPTION';
    public const DocPlaceholderName PlaceholderSenderName = 'SENDERNAME';
    public const DocPlaceholderName PlaceholderSenderEmail = 'SENDEREMAIL';
    public const DocPlaceholderName PlaceholderCompanyId = 'COMPANYID';
    public const DocPlaceholderName PlaceholderCompanyName = 'COMPANYNAME';
    public const DocPlaceholderName PlaceholderCompanyLogo = 'COMPANYLOGO';
    public const DocPlaceholderName PlaceholderUserId = 'USERID';
    public const DocPlaceholderName PlaceholderUserName = 'USERNAME';
    public const DocPlaceholderName PlaceholderUserAlias = 'USERALIAS';
    public const DocPlaceholderName PlaceholderUserEmail = 'USEREMAIL';
    public const DocPlaceholderName PlaceholderWorker = 'WORKER';
    public const DocPlaceholderName PlaceholderWorkerTitle = 'WORKERTITLE';
    public const DocPlaceholderName PlaceholderWorkerDepartment = 'WORKERDEPARTMENT';
    public const DocPlaceholderName PlaceholderWorkerEmail = 'WORKEREMAIL';
    public const DocPlaceholderName PlaceholderWorkerImage = 'WORKERIMAGE';
    public const DocPlaceholderName PlaceholderCurrentDate = 'CURRENTDATE';
    public const DocPlaceholderName PlaceholderCurrentDatetime = 'CURRENTDATETIME';
