    public const DocPlaceholderName PlaceholderFirstComment = 'FIRSTCOMMENT';
    public const DocPlaceholderName PlaceholderLastComment = 'LASTCOMMENT';
    public const DocPlaceholderName PlaceholderAllComments = 'ALLCOMMENTS';
    public const DocPlaceholderName PlaceholderEnvironmentName = 'ENVNAME';
    public const DocPlaceholderName PlaceholderEnvironmentDomainName = 'ENVDOMAINNAME';
