public void applyColor()
{        
    EcoResProductMasterColor    ecoResProductMasterColor;
    Integer                     counter, i;
    SysOperationProgress        progress = new SysOperationProgress();
    TmpABC                      localColors;
    ;

    localColors.setTmpData(colors);
    counter = int642int((select count(RecId) from ecoResProductMasterColor).RecId);

    progress.setCaption('Applying sorting for product Colors...');        
    progress.setTotal(counter);

    while select forUpdate ecoResProductMasterColor
        order by ColorProductMaster
    {
        select firstOnly localColors
            where localColors.ItemName == EcoResColor::find(ecoResProductMasterColor.Color).Name;

        ecoResProductMasterColor.SC_SortNumColor = real2int(localColors.Amount);
        ecoResProductMasterColor.Update();

        progress.setText(strfmt('%1 colors updated', i));
        progress.setCount(i);
        i++;
    }

    progress.hide();
    Box::info('Sorting complete.');
}

