public void applySize()
{        
    EcoResProductMasterSize ecoResProductMasterSize;
    Integer                 counter, i;
    SysOperationProgress    progress = new SysOperationProgress();
    TmpABC                  localSizes;
    String255               s;
    ;

    localSizes.setTmpData(sizes);
    counter = int642int((select count(RecId) from ecoResProductMasterSize).RecId);

    progress.setCaption('Applying sorting for product sizes...');        
    progress.setTotal(counter);

    while select forUpdate ecoResProductMasterSize
        order by SizeProductMaster
    {
        select firstOnly localSizes
            where localSizes.ItemName == EcoResSize::find(ecoResProductMasterSize.Size).Name;

        s = localSizes.ItemName;
        ecoResProductMasterSize.SC_SortNumSize = real2int(localSizes.Amount);
        ecoResProductMasterSize.Update();

        progress.setText(strfmt('%1 sizes updated', i));
        progress.setCount(i);
        i++;
    }

    progress.hide();
    Box::info('Sorting complete.');
}

