public void fillColorsData()
{
    EcoResColor              ecoResColor, ecoResColorName;
    EcoResProductMasterColor ecoResProductMasterColor;
    int                      i;
    ;

    if(!colors)
    {            
        while select Name from ecoResColorName
            group by Name
            order by Name
        {
            select firstOnly SC_SortNumColor from EcoResProductMasterColor
                order by SC_SortNumColor desc
                join EcoResColor
                    where EcoResProductMasterColor.Color == EcoResColor.RecId &&
                          EcoResColor.Name == EcoResColorName.Name;

            colors.ItemName = EcoResColorName.Name;
            colors.Amount   = EcoResProductMasterColor.SC_SortNumColor;
            colors.RefRecId = i;
            colors.insert();
            i++;
        }

        while select forUpdate colors
        order by Amount desc, ItemName
        {
            colors.Amount = i;
            colors.update();
            i--;
        }           
    }
}

public void fillConfigData()
{
    EcoResConfiguration              ecoResConfiguration, ecoResConfigurationName;
    EcoResProductMasterConfiguration ecoResProductMasterConfiguration;
    int                              i;
    ;

    if(!configurations)
    {            
        while select Name from ecoResConfigurationName
            group by Name
