public void upDown(NoYes _down, TmpABC _buffer)
{
    TmpABC    update;
    LineNum   newNum;
    ;

    update.setTmpData(_buffer);

    if (_down == NoYes::Yes)
    {
        select firstonly forupdate update
            order by Amount
            where update.Amount > _buffer.Amount;
    }
    else
    {
        select firstonly forupdate update
            order by Amount desc
            where update.Amount < _buffer.Amount;
    }

    if (update && _buffer)
    {
        newNum          = update.Amount;
        update.Amount   = _buffer.Amount;
        _buffer.Amount  = newNum;

        _buffer.update();
        update.update();
    }
}

public void executeQuery()
{
    EcoResSize              EcoResSize, EcoResSizeName;
    EcoResProductMasterSize EcoResProductMasterSize;
    int                     i;
    ;

    if(!sizes)
    {                
        while select Name from EcoResSizeName
            group by Name
            order by Name
        {
            select firstOnly SC_SortNumSize from EcoResProductMasterSize
            order by SC_SortNumSize desc
            join EcoResSize
                where EcoResProductMasterSize.Size == EcoResSize.RecId &&
                      EcoResSize.Name == EcoResSizeName.Name;

            Sizes.ItemName = EcoResSizeName.Name;
            Sizes.Amount   = EcoResProductMasterSize.SC_SortNumSize;
            Sizes.RefRecId = i;
            Sizes.insert();
            i++;
        }

        while select forUpdate sizes
            order by Amount desc, ItemName
        {
            sizes.Amount = i;
            sizes.update();
            i--;
        }                
    }

    Sizes_ds.query().dataSourceTable(tableNum(TmpABC)).addOrderByField(fieldNum(TmpABC, Amount), SortOrder::Ascending);

    super();
}

