Method.Source:
//BP deviation documented
[SysClientCacheDataMethodAttribute(true)]
display Description displayFilterName()
{
    SC_FilterWebShopsTable FilterWebShopsTable;
    SC_LargeText           ret;

    while select FieldName from FilterWebShopsTable
        where FilterWebShopsTable.WebShopId == this.WebShopId
    {
        ret = ret ? ret + ',' + FilterWebShopsTable.FieldName : FilterWebShopsTable.FieldName;
    }

    return ret;
}

public void modifiedField(FieldId _fieldId)
{
    super(_fieldId);

    switch (_fieldId)
    {
        case fieldnum(SC_WebShopsTable, EnableProdCategories):
            Box::yesNo("@SCL262", DialogButton::Yes);
            break;

        case fieldnum(SC_WebShopsTable, OrderType):
            if(this.OrderType == SC_Salestype::Quotation)
            {
                this.SyncOrderType = SC_SalesType::Quotation;
            }
            break;
    }
}

public boolean validateField(FieldId _fieldIdToCheck)
{
    boolean ret;

    ret = super(_fieldIdToCheck);

    switch (_fieldIdToCheck)
    {
        case fieldNum(SC_WebShopsTable, EnableRetail):
            if(ret && this.EnableRetail && !RetailParameters::isRetailEnabledAndInUse())
            {
                ret = checkFailed("@SCL310");
            }

            if(ret && this.EnableRetail && this.OrderType == SC_SalesType::Quotation && !this.EnableQuoteForRetail)
            {
                ret = checkFailed("@SCL554");
            }
            break;

        case fieldNum(SC_WebShopsTable, OrderType):
            if(ret && this.EnableRetail && this.OrderType == SC_SalesType::Quotation && !this.EnableQuoteForRetail)
            {
                ret = checkFailed("@SCL554");
            }
            break;

        case fieldNum(SC_WebShopsTable, MaxMultiThreads):
            if(ret && (this.MaxMultiThreads < 2 || this.MaxMultiThreads > 10))
            {
                ret = checkFailed(strFmt("@SYS344093", fieldPName(SC_WebShopsTable, MaxMultiThreads), 2, 10));
            }
            break;
    }

    return ret;
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if(ret && this.EnableProdCategories && !this.CategoryHierarchy)
    {
        ret = checkFailed("@SCL260");
    }

    if(ret && this.OverrideCustDim && (!this.DefaultLocationId || !this.DefaultSiteId))
    {
        ret = checkFailed("@SCL273");
    }

    if(ret && (this.DefaultSiteId || this.DefaultLocationId))
    {
        if((select firstonly InventLocation
             where InventLocation.InventLocationId == this.DefaultLocationId &&
                   InventLocation.InventSiteId == this.DefaultSiteId).RecId == 0)
        {
            ret = checkFailed(strfmt("@SCL101", this.DefaultLocationId, this.DefaultSiteId));
        }
    }

    if(ret && TaxParameters::find().CheckIntersection == CheckTaxGroups::Error && !this.DefaultItemSalesTaxGroup)
    {
        ret = checkFailed("@SCL268");
    }

    if(ret && this.OrderType == SC_SalesType::Quotation && this.SyncOrderType != SC_SalesType::Quotation)
    {
        ret = checkFailed(strFmt("@SCL311", fieldPName(SC_WebShopsTable, SyncOrderType)));
    }

    if(RetailSMB::IsRetailSMBEnabled() && !this.EnableRetail)
    {
        this.EnableRetail = true;
    }

    if(ret && isConfigurationkeyEnabled(configurationKeyNum(Retail)) && this.EnableRetail)
    {
        if(!this.RetailStore)
        {
            ret = checkFailed(strFmt("@SYS117590", fieldpname(SC_WebshopsTable, RetailStore)));
        }

        if(!this.RetailTerminalId)
        {
            ret = checkFailed(strFmt("@SYS117590", fieldpname(SC_WebshopsTable, RetailTerminalId)));
        }
    }

    if(ret && isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)) && this.CompleteRetailOrder && !this.RetailPaymMethod)
    {
        warning("@SCL523");
    }

    if(ret && this.RetailPaymMethod && this.orig().RetailPaymMethod != this.RetailPaymMethod)
    {
        info("@SCL524");
    }

    return ret;
}

static SC_WebShopsTable find(SC_WebShopId _webShopId, boolean _forUpdate = false)
{
    SC_WebShopsTable  webShopsTable;        

    webShopsTable.selectForUpdate(_forUpdate);

    if (_webShopId)
    {
        select firstonly WebShopsTable
        where WebShopsTable.webShopId == _webShopId;
    }

    return WebShopsTable;
}

static SC_WebShopsTable findFirst()
{
    SC_WebShopsTable    SC_WebShopsTable;        

    select firstonly SC_WebShopsTable;

    return SC_WebShopsTable;
}

public String15 getCompatibilityLevel()
{
    #SANA
    String15 compatibilityLevel;

    switch(this.CompatibilityLevel)
    {
        case SC_CompatibilityLevel::SCC100:
            compatibilityLevel = 'SCC 1.0.0';
            break;
        case SC_CompatibilityLevel::SC935:
            compatibilityLevel = '9.3.5';
            break;
        case SC_CompatibilityLevel::SC934:
            compatibilityLevel = '9.3.4';
            break;
        case SC_CompatibilityLevel::SC933:
            compatibilityLevel = '9.3.3';
            break;
        case SC_CompatibilityLevel::SC930:
            compatibilityLevel = '9.3.0';
            break;
        default:
            compatibilityLevel = #compatibilityVersion;
            break;
    }

    return compatibilityLevel;
}

static public NumberSequenceTable lookupNSCode(FormReferenceControl _formReferenceControl)
{
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(NumberSequenceTable), _formReferenceControl);
    NumberSequenceTable     selection;

    sysTableLookup.addLookupfield(fieldNum(NumberSequenceTable, NumberSequence));
    sysTableLookup.addLookupfield(fieldNum(NumberSequenceTable, Txt));
    sysTableLookup.addLookupfield(fieldNum(NumberSequenceTable, RecId));

    selection = sysTableLookup.performFormLookup();

    return selection;
}

static public void lookupMainAccount(FormStringControl _formControl, MainAccountNum _accountNum)
{
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    SysTableLookup          sysTableLookup = SysTableLookup::newParameters(tablenum(MainAccount), _formControl);
    RecId                   queryCOAValue;        

    queryCOAValue = LedgerChartOfAccounts::current();    

    sysTableLookup.addLookupfield(fieldnum(MainAccount,MainAccountId));
    sysTableLookup.addLookupfield(fieldnum(MainAccount,Name));
    sysTableLookup.addLookupfield(fieldnum(MainAccount,Type));

    queryBuildDataSource = query.addDataSource(tablenum(MainAccount));
    //not all GL accounts are applicable for charges
    //queryBuildDataSource.addRange(fieldnum(MainAccount,AccountCategoryRef)).value(Sysquery::valueNotEmptyString());

    MainAccount::addSecureRangeForChartOfAccounts(queryBuildDataSource, queryCOAValue);

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

static void lookupTerminalId(FormStringControl _formControl, RetailStoreId _storeId)
{
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    SysTableLookup          sysTableLookup = SysTableLookup::newParameters(tablenum(RetailTerminalTable), _formControl);        

    sysTableLookup.addLookupfield(fieldnum(RetailTerminalTable, TerminalId));
    sysTableLookup.addLookupfield(fieldnum(RetailTerminalTable, Name));

    queryBuildDataSource = query.addDataSource(tablenum(RetailTerminalTable));
    queryBuildDataSource.addRange(fieldnum(RetailTerminalTable, StoreRecId)).value(queryValue(RetailStoreTable::find(_storeId).RecId));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

