static public void lookupPaymentJournal(FormStringControl _formControl)
{
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource = query.addDataSource(tableNum(LedgerJournalName));
    SysTableLookup          sysTableLookup = SysTableLookup::newParameters(tableNum(LedgerJournalName), _formControl);        

    sysTableLookup.addLookupfield(fieldnum(LedgerJournalName,JournalName));
    sysTableLookup.addLookupfield(fieldnum(LedgerJournalName,Name));
    sysTableLookup.addLookupfield(fieldnum(LedgerJournalName,JournalType));

    queryBuildDataSource.addRange(fieldNum(LedgerJournalName, JournalType)).value(enum2str(LedgerJournalType::CustPayment));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

static public void lookupCallCenterPaymMethods(FormStringControl _formControl)
{
    SysTableLookup       sysTableLookup = SysTableLookup::newParameters(tablenum(RetailTenderTypeTable), _formControl);
    Query                query = new Query();
    QueryBuildDataSource queryBuildDataSource = query.addDataSource(tablenum(RetailTenderTypeTable));

    sysTableLookup.addLookupfield(fieldnum(RetailTenderTypeTable, tenderTypeId), true);
    sysTableLookup.addLookupfield(fieldnum(RetailTenderTypeTable, name));

    queryBuildDataSource.addRange(fieldnum(RetailTenderTypeTable, defaultFunction)).value(queryValue(RetailTenderFunction::Customer));
    queryBuildDataSource.addRange(fieldnum(RetailTenderTypeTable, defaultFunction)).value(queryValue(RetailTenderFunction::Normal));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

